out = {"cities":[]}
for city in cities:
    g = geocoder.osm(city+",India")
    if g is not None and g.json is not None and g.json['lat'] is not None and g.json['lng'] is not None:
        out["cities"].append({
            "city":city,
            "lat":g.json['lat'],
            "lng":g.json['lng'],
        })
    else:
        print("No Location found for city : "+city)

# Serializing json 
json_object = json.dumps(out, indent = 4)
  
# Writing to sample.json
with open("cities.json", "w") as outfile:
    outfile.write(json_object)